"use client"

import * as React from "react"
import { cn } from "@/lib/utils"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import {
  LineChart,
  Line,
  ResponsiveContainer,
  Tooltip,
} from "recharts"
import { TrendingUp, TrendingDown, Minus, Activity } from "lucide-react"

export type HealthStatus = "healthy" | "warning" | "critical" | "neutral"

export interface HealthMetric {
  id: string
  label: string
  value: number
  unit: string
  status: HealthStatus
  trend: number
  sparkline: { value: number }[]
  target?: { min: number; max: number }
}

interface HealthMatrixProps extends React.HTMLAttributes<HTMLDivElement> {
  metrics: HealthMetric[]
  columns?: 2 | 3 | 4
}

const statusColors: Record<HealthStatus, string> = {
  healthy: "text-green-600 dark:text-green-400",
  warning: "text-yellow-600 dark:text-yellow-400",
  critical: "text-red-600 dark:text-red-400",
  neutral: "text-muted-foreground",
}

const statusBadgeVariant: Record<HealthStatus, "success" | "warning" | "destructive" | "secondary"> = {
  healthy: "success",
  warning: "warning",
  critical: "destructive",
  neutral: "secondary",
}

const sparklineColors: Record<HealthStatus, string> = {
  healthy: "#22c55e",
  warning: "#eab308",
  critical: "#ef4444",
  neutral: "#6b7280",
}

function TrendIcon({ trend }: { trend: number }) {
  if (trend > 0) return <TrendingUp className="h-3 w-3" />
  if (trend < 0) return <TrendingDown className="h-3 w-3" />
  return <Minus className="h-3 w-3" />
}

function MetricCard({ metric }: { metric: HealthMetric }) {
  return (
    <Card className="relative overflow-hidden">
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
        <CardTitle className="text-sm font-medium text-muted-foreground">
          {metric.label}
        </CardTitle>
        <Badge variant={statusBadgeVariant[metric.status]}>
          {metric.status}
        </Badge>
      </CardHeader>
      <CardContent>
        <div className="flex items-baseline gap-2">
          <span className={cn("text-3xl font-bold", statusColors[metric.status])}>
            {metric.value}
          </span>
          <span className="text-sm text-muted-foreground">{metric.unit}</span>
        </div>
        {metric.target && (
          <p className="text-xs text-muted-foreground mt-1">
            Target: {metric.target.min}–{metric.target.max} {metric.unit}
          </p>
        )}
        <div className="flex items-center justify-between mt-3">
          <div className={cn("flex items-center gap-1 text-xs", statusColors[metric.status])}>
            <TrendIcon trend={metric.trend} />
            <span>{metric.trend > 0 ? "+" : ""}{metric.trend}%</span>
          </div>
          <div className="h-8 w-20">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={metric.sparkline}>
                <Tooltip
                  content={({ payload }) => {
                    if (payload && payload.length) {
                      return (
                        <div className="rounded bg-popover px-2 py-1 text-xs shadow">
                          {payload[0].value}
                        </div>
                      )
                    }
                    return null
                  }}
                />
                <Line
                  type="monotone"
                  dataKey="value"
                  stroke={sparklineColors[metric.status]}
                  strokeWidth={1.5}
                  dot={false}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </CardContent>
    </Card>
  )
}

const colClasses = {
  2: "grid-cols-1 sm:grid-cols-2",
  3: "grid-cols-1 sm:grid-cols-2 lg:grid-cols-3",
  4: "grid-cols-1 sm:grid-cols-2 lg:grid-cols-4",
}

function HealthMatrix({ metrics, columns = 3, className, ...props }: HealthMatrixProps) {
  return (
    <div className={cn("space-y-4", className)} {...props}>
      <div className="flex items-center gap-2 text-sm text-muted-foreground">
        <Activity className="h-4 w-4" />
        <span>Health Matrix</span>
        <span className="ml-auto">{metrics.length} metrics</span>
      </div>
      <div className={cn("grid gap-4", colClasses[columns])}>
        {metrics.map((metric) => (
          <MetricCard key={metric.id} metric={metric} />
        ))}
      </div>
    </div>
  )
}

export { HealthMatrix }

"use client"

import * as React from "react"
import { cn } from "@/lib/utils"
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card"
import { Sankey, Tooltip, ResponsiveContainer } from "recharts"

export interface SankeyNode {
  name: string
  color?: string
}

export interface SankeyLink {
  source: number
  target: number
  value: number
  color?: string
}

export interface SankeyData {
  nodes: SankeyNode[]
  links: SankeyLink[]
}

interface SankeyChartProps extends React.HTMLAttributes<HTMLDivElement> {
  data: SankeyData
  title?: string
  description?: string
  height?: number
  nodePadding?: number
  nodeWidth?: number
}

function SankeyTooltip({ active, payload }: any) {
  if (active && payload && payload.length) {
    const data = payload[0]
    if (data.payload.source && data.payload.target) {
      return (
        <div className="rounded-lg border bg-popover px-3 py-2 text-sm shadow-md">
          <p className="font-medium">
            {data.payload.source.name} → {data.payload.target.name}
          </p>
          <p className="text-muted-foreground">
            Value: {data.payload.value.toLocaleString()}
          </p>
        </div>
      )
    }
    if (data.payload.name) {
      return (
        <div className="rounded-lg border bg-popover px-3 py-2 text-sm shadow-md">
          <p className="font-medium">{data.payload.name}</p>
          <p className="text-muted-foreground">
            Total: {data.payload.value.toLocaleString()}
          </p>
        </div>
      )
    }
  }
  return null
}

function SankeyChart({
  data,
  title,
  description,
  height = 400,
  nodePadding = 24,
  nodeWidth = 10,
  className,
  ...props
}: SankeyChartProps) {
  return (
    <Card className={cn(className)} {...props}>
      {(title || description) && (
        <CardHeader>
          {title && <CardTitle>{title}</CardTitle>}
          {description && <CardDescription>{description}</CardDescription>}
        </CardHeader>
      )}
      <CardContent>
        <div style={{ height }}>
          <ResponsiveContainer width="100%" height="100%">
            <Sankey
              data={data}
              nodePadding={nodePadding}
              nodeWidth={nodeWidth}
              margin={{ top: 10, right: 10, bottom: 10, left: 10 }}
            >
              <Tooltip content={<SankeyTooltip />} />
            </Sankey>
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  )
}

export { SankeyChart }

"use client"

import * as React from "react"
import { cn } from "@/lib/utils"
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import {
  AreaChart,
  Area,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Legend,
} from "recharts"
import { TrendingUp, TrendingDown, Users, DollarSign, Eye, ShoppingCart } from "lucide-react"

export interface KPIData {
  label: string
  value: string
  change: number
  icon: "users" | "revenue" | "views" | "conversions"
}

export interface ChartDataPoint {
  name: string
  [key: string]: string | number
}

export interface AnalyticsConfig {
  kpis: KPIData[]
  areaChart: {
    title: string
    data: ChartDataPoint[]
    series: { key: string; color: string; label: string }[]
  }
  barChart: {
    title: string
    data: ChartDataPoint[]
    series: { key: string; color: string; label: string }[]
  }
}

interface AnalyticsDashboardProps extends React.HTMLAttributes<HTMLDivElement> {
  config: AnalyticsConfig
  timeRanges?: string[]
  defaultTimeRange?: string
  onTimeRangeChange?: (range: string) => void
}

const iconMap = {
  users: Users,
  revenue: DollarSign,
  views: Eye,
  conversions: ShoppingCart,
}

function KPICard({ kpi }: { kpi: KPIData }) {
  const Icon = iconMap[kpi.icon]
  const isPositive = kpi.change >= 0

  return (
    <Card>
      <CardContent className="p-6">
        <div className="flex items-center justify-between">
          <div className="rounded-lg bg-muted p-2">
            <Icon className="h-5 w-5 text-muted-foreground" />
          </div>
          <Badge variant={isPositive ? "success" : "destructive"} className="gap-1">
            {isPositive ? <TrendingUp className="h-3 w-3" /> : <TrendingDown className="h-3 w-3" />}
            {isPositive ? "+" : ""}{kpi.change}%
          </Badge>
        </div>
        <div className="mt-3">
          <p className="text-2xl font-bold">{kpi.value}</p>
          <p className="text-sm text-muted-foreground">{kpi.label}</p>
        </div>
      </CardContent>
    </Card>
  )
}

function ChartTooltip({ active, payload, label }: any) {
  if (active && payload && payload.length) {
    return (
      <div className="rounded-lg border bg-popover px-3 py-2 text-sm shadow-md">
        <p className="font-medium mb-1">{label}</p>
        {payload.map((entry: any, i: number) => (
          <p key={i} style={{ color: entry.color }}>
            {entry.name}: {entry.value.toLocaleString()}
          </p>
        ))}
      </div>
    )
  }
  return null
}

function AnalyticsDashboard({
  config,
  timeRanges = ["7d", "30d", "90d", "1y"],
  defaultTimeRange = "30d",
  onTimeRangeChange,
  className,
  ...props
}: AnalyticsDashboardProps) {
  const [activeRange, setActiveRange] = React.useState(defaultTimeRange)

  function handleRangeChange(range: string) {
    setActiveRange(range)
    onTimeRangeChange?.(range)
  }

  return (
    <div className={cn("space-y-6", className)} {...props}>
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold tracking-tight">Analytics</h2>
          <p className="text-muted-foreground">Overview of your key metrics</p>
        </div>
        <div className="flex gap-1">
          {timeRanges.map((range) => (
            <Button
              key={range}
              variant={activeRange === range ? "default" : "outline"}
              size="sm"
              onClick={() => handleRangeChange(range)}
            >
              {range}
            </Button>
          ))}
        </div>
      </div>

      <div className="grid gap-4 grid-cols-1 sm:grid-cols-2 lg:grid-cols-4">
        {config.kpis.map((kpi, i) => (
          <KPICard key={i} kpi={kpi} />
        ))}
      </div>

      <div className="grid gap-4 grid-cols-1 lg:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle>{config.areaChart.title}</CardTitle>
            <CardDescription>Trend over selected period</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="h-[300px]">
              <ResponsiveContainer width="100%" height="100%">
                <AreaChart data={config.areaChart.data}>
                  <CartesianGrid strokeDasharray="3 3" className="stroke-muted" />
                  <XAxis dataKey="name" className="text-xs" />
                  <YAxis className="text-xs" />
                  <Tooltip content={<ChartTooltip />} />
                  <Legend />
                  {config.areaChart.series.map((s) => (
                    <Area
                      key={s.key}
                      type="monotone"
                      dataKey={s.key}
                      name={s.label}
                      stroke={s.color}
                      fill={s.color}
                      fillOpacity={0.1}
                      strokeWidth={2}
                    />
                  ))}
                </AreaChart>
              </ResponsiveContainer>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>{config.barChart.title}</CardTitle>
            <CardDescription>Comparison over selected period</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="h-[300px]">
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={config.barChart.data}>
                  <CartesianGrid strokeDasharray="3 3" className="stroke-muted" />
                  <XAxis dataKey="name" className="text-xs" />
                  <YAxis className="text-xs" />
                  <Tooltip content={<ChartTooltip />} />
                  <Legend />
                  {config.barChart.series.map((s) => (
                    <Bar
                      key={s.key}
                      dataKey={s.key}
                      name={s.label}
                      fill={s.color}
                      radius={[4, 4, 0, 0]}
                    />
                  ))}
                </BarChart>
              </ResponsiveContainer>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  )
}

export { AnalyticsDashboard }
